mapper.select:
select

from miss_control_task_records

	
		taskTitle like CONCAT('%',#{taskTitle,jdbcType=VARCHAR},'%')
	
	
		and taskType = #{taskType,jdbcType=VARCHAR}
	
	
		and taskStatus in
		
			#{status}
		
	
	
		and taskMenuType = #{taskMenuType,jdbcType=VARCHAR}
	
	
		and taskcreaterusercode in
		
			#{userCode}
		
	
	
		and taskfirsttrialcode in
		
			#{userCode}
		
	
	
		and tasksecondtrialcode in
		
			#{userCode}
		
	
	
		and taskfinaltrialcode in
		
			#{userCode}
		
	
	
		and taskCreateTime >= #{taskCreateTimeBegin,jdbcType=TIMESTAMP}
	
	
		and taskCreateTime <= #{taskCreateTimeEnd,jdbcType=TIMESTAMP}
	
	
		and taskFirstTrialTime >= #{taskFirstTrialTimeBegin,jdbcType=TIMESTAMP}
	
	
		and taskFirstTrialTime <= #{taskFirstTrialTimeEnd,jdbcType=TIMESTAMP}
	
	
		and taskSecondTrialTime >= #{taskSecondTrialTimeBegin,jdbcType=TIMESTAMP}
	
	
		and taskSecondTrialTime <= #{taskSecondTrialTimeEnd,jdbcType=TIMESTAMP}
	
	
		and taskFinalTrialTime >= #{taskFinalTrialTimeBegin,jdbcType=TIMESTAMP}
	
	
		and taskFinalTrialTime <= #{taskFinalTrialTimeEnd,jdbcType=TIMESTAMP}
	
	
		and updateTime >= #{updateTimeBegin,jdbcType=TIMESTAMP}
	
	
		and updateTime <= #{updateTimeEnd,jdbcType=TIMESTAMP}
