mapper.select:
	select b.id as id, b.medical_type as medical_type, b.com_name as com_name 
	from miss_symptom_medical_mapping a left join miss_medical b on a.medical_id  = b.id
	where a.symptom_id=#{id}

select

,

from miss_medical

	
		com_name = #{comName,jdbcType=VARCHAR }
	
	
		or english_name = #{englishName,jdbcType=VARCHAR}
	
	
		or chinese_pinyin = #{chinesePinyin,jdbcType=VARCHAR}
	
	
		or shop_name = #{shopName,jdbcType=VARCHAR}
